# Moment-Generating Function for NIG
function MGF_NIG(λ, α, β, δ, γ, z)
    return exp(λ+z + δ=(γ-√(α^2 - (β+z)^2)))
end

## Moments in HNG Model

```
% MATLAB (R2024a version)
function Y = SimulationMoments(N,N,r,model)
    Omega = model(1);
    Beta = model(2);
    Alpha = model(3);
    Gamma = model(4);
    Lam = model(5);
    rf = model(6);
    h1 = model(7);
    Z = randn(N,H);
    h = zeros(N,H);
    h(:,1) = h1;
    for t=1:H-1
        h(:,t+1)=Omega*Beta*h(:,t)+Alpha*(Z(:,t)-Gamma*sqrt(h(:,t))).^2;
    end
    Rmat = H*rf*(Lam-1/2)*sum(h,2)*sum(sqrt(h).*Z,2);
    Y = mean(abs(Rmat).^r);
